import { useState, useEffect } from 'react';
import './RecipesDetail.css';
import { useParams } from 'react-router-dom';

function RecipesDetail() {

    const {id} = useParams();
    const [oneRecipe, setOneRecipe] = useState([]);

    useEffect(() => {
       fetch(`http://localhost:8000/recipes/${id}`)
       .then((res) => {
            return res.json();
        })
        .then((data) => {
            setOneRecipe(data.recipe);
        });
      }, []);

    return(
      <main className="mainSection">
         { oneRecipe.map((item) => {
           return(
              <article className="mainArticle" key={ item.id }>
                  <h1>{ item.title }</h1>
                  <div className="mainWallpaper">Slika</div>
                  <h3 className="mainDescription">Sastojci</h3>
                  <div className="mainList">
                      <ul>
                          <li>20 jaja</li>
                          <li>bele kore</li>
                          <li>banane</li>
                          <li>kokos</li>
                      </ul>
                  </div>
                  <h3 className="mainDescription">Postupak</h3>
                  <p>Lorem ipsum, Lorem ipsum Lorem ipsum Lorem ipsum Lorem ipsum Lorem ipsum Lorem ipsum Lorem ipsumLorem ipsum</p>
                  <p>Autor: Dusan</p>
                  <div>{ id }</div>
              </article>
           )
         })}
      </main>
    );
};

export default RecipesDetail;
