import { useEffect, useState } from 'react';
import { Form, useParams } from 'react-router-dom';

function EditRecipe() {

    let { id } = useParams();
    let [recipe, setRecipe] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8000/recipes/${id}`)
        .then((res) => res.json())
        .then((data) => setRecipe(data.recipe));
    })

    function editRecipe(e) {
        e.preventDefault();
        console.log("Radi");
    }

    return(
        <main className="mainSection">
            
            { recipe && recipe.map((item) => {
                return(
                    <article className="mainArticle" key={ item.id }>
                       <h1>Edit recipe</h1>
                        <form onSubmit={ editRecipe } className="editRecipe">
                            <input type="number" value={item.id}></input><br />
                            <input type="text" value={item.title}></input><br />
                            <input type="text" value={item.text}></input><br />
                            <input type="text" value={item.author}></input><br />
                            <button>Save</button>
                        </form>
                    </article>
                )
            })}
            
        </ main>
    );
};

export default EditRecipe;
