import { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';

function RecipesDetail() {

    const {id} = useParams();
    const [oneRecipe, setOneRecipe] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
       fetch(`http://localhost:8000/recipes/${id}`)
        .then((res) => {
              return res.json();
          })
          .then((data) => {
              setOneRecipe(data.recipe);
          });
      }, []);

      function deleteRecipe() {
        fetch(`http://localhost:8000/recipes/${id}`, {
          method: 'DELETE'
        })
         .then((res) => res.json())
         .then(() => window.location.href = "/");

        //  return navigate("/");
      }

    return(
      <main className="mainSection">
         { oneRecipe && oneRecipe.map((item) => {
           return(
              <article className="mainArticle" key={ item.id }>
                  <h1>{ item.title }</h1>

                  { item.wallpaper != null ? (
                    <div className="mainWallpaper"><img src={ `/images/${ item.wallpaper }`} alt="image_recipe" /></div>
                  ) : (
                    <div className="mainWallpaper"><img src={ `/images/no_image.png`} alt="no_recipe_image" /></div>
                  )}
                  
                  <h3 className="mainDescription">Sastojci</h3>
                  <div className="mainList">
                      <ul>
                          <li>20 jaja</li>
                          <li>bele kore</li>
                          <li>banane</li>
                          <li>kokos</li>
                      </ul>
                  </div>
                  <h3 className="mainDescription">Postupak</h3>
                  <p>{ item.text }</p>
                  <p>Autor: { item.author }</p>
                  
                  <Link to={"/recipes/edit/"+ item.id}><button>Edit</button></Link>
                  <button onClick={ deleteRecipe }>Delete</button>
              </article>
           )
         })}
      </main>
    );
};

export default RecipesDetail;
