import './AddRecipe.css';
import { useState } from 'react';

function AddRecipe() {

    const [title, setTitle] = useState("");
    const [components, setComponents] = useState("");
    const [text, setText] = useState("");
    const [author, setAuthor] = useState("");
    const [wallpaper, setWallpaper] = useState("");
    const [message, setMessage] = useState("");

    const recipe = { title, components, text, author, wallpaper };

    function recipeAdd(e) {
        e.preventDefault();
        // alert("Hello World");
        // console.log(recipe);
        const blog = { title:title, components:components, text:text, author:author, wallpaper:wallpaper };
        fetch('http://localhost:8000/add', {
            method: 'POST',
            headers: { "Content-type": "application/json"},
            body: JSON.stringify(blog)
        }).then((res) => {
            return res.json();
            // console.log("New blog added");
          
        }).then((data) => console.log(data));

        setMessage("You insert a new recipe !");
    }

    return(
        <main className="mainSection">
            <article className="mainArticle">
                <h1>Add new recipe</h1>
                <form id="addRecipeForm" onSubmit ={ recipeAdd }>
                    <input type="text" id="title" placeholder="Enter title" onChange={ (e) => setTitle(e.target.value) } required></input><br />
                    <input type="text" id="components" placeholder="Enter components" onChange={ (e) => setComponents(e.target.value) } required></input><br />
                    <textarea type="text" id="text" placeholder="Enter text" onChange={ (e) => setText(e.target.value) } required></textarea><br />
                    <input type="text" id="author" placeholder="Enter author" onChange={ (e) => setAuthor(e.target.value) } required></input><br />
                    <input type="file" id="wallpaper" accept="image/png, image/jpeg, image/jpg" onChange={ (e) => setWallpaper('/images/' + e.target.files[0].name )}></input><br />
                    <button>Add</button>
                </form>
            </article>
        </main> 
    );
};

export default AddRecipe;
