import './AddRecipe.css';
import { useState } from 'react';

function AddRecipe() {

    const [title, setTitle] = useState("");
    const [s, setS] = useState("");
    const [text, setText] = useState("");
    const [author, setAuthor] = useState("");
    // let wall = document.getElementById("wallpaper").name;
    const [wall, setWall] = useState("");

    const recipe = { title, s, text, author };

    function recipeAdd(e) {
        e.preventDefault();
        // alert("Hello World");
        // console.log(recipe);
        const blog = { title:title, components:s, text:text, author:author};
        fetch('http://localhost:8000/add', {
            method: 'POST',
            headers: { "Content-type": "application/json"},
            body: JSON.stringify(blog)
        }).then((res) => {
            return res.json();
            // console.log("New blog added");
          
        }).then((data) => console.log(data));
    }

    function image() {
        console.log(wall);
    }

    return(
        <main className="mainSection">
            <article className="mainArticle">
                <h1>Add new recipe</h1>
                <form id="addRecipeForm" onSubmit ={ recipeAdd }>
                    <input type="text" id="title" placeholder="Enter title" onChange={ (e) => setTitle(e.target.value) } required></input><br />
                    <input type="text" id="s" placeholder="Enter s" onChange={ (e) => setS(e.target.value) } required></input><br />
                    <input type="text" id="text" placeholder="Enter text" onChange={ (e) => setText(e.target.value) } required></input><br />
                    <input type="text" id="author" placeholder="Enter author" onChange={ (e) => setAuthor(e.target.value) } required></input><br />
                    <input type="file" id="wallpaper" accept="image/png, image/jpeg, image/jpg"></input><br />
                    <button>Add</button>
                    <button onClick = { image }>Image</button>
                </form>
            </article>
        </main> 
    );
};

export default AddRecipe;
