import './EditRecipe.css';
import { useEffect, useState } from 'react';
import { Form, useParams } from 'react-router-dom';

function EditRecipe() {

    let { id } = useParams();
    let [recipe, setRecipe] = useState(null);

    const [ upTitle, setUpTitle ] = useState("");
    const [ upComponents, setUpComponents ] = useState("");
    const [ upText, setUpText ] = useState("");
    const [ upAuthor, setUpAuthor ] = useState("");

    const upObj = {upTitle, upComponents, upText, upAuthor};

    useEffect(() => {
        fetch(`http://localhost:8000/recipes/${id}`)
        .then((res) => res.json())
        .then((data) => {
            setRecipe(data.recipe);
                data.recipe.forEach((item) => {
                    setUpTitle(item.title);
                    setUpComponents(item.components);
                    setUpText(item.text);
                    setUpAuthor(item.author);
                });
        })
    })

    function editRecipe(e) {
        e.preventDefault();
        console.log("Radi");
        console.log(upObj);
        console.log(upTitle);
    }

    return(
        <main className="mainSection">
            
            { recipe && recipe.map((item) => {
                return(
                    <article className="mainArticle" key={ item.id }>
                       <h1>Edit recipe</h1>
                        <form onSubmit={ editRecipe } className="editRecipe">
                            <input type="text" value={item.id} disabled></input><br />
                            <input type="text" value={item.title} onChange={(e) => setUpTitle(e.target.value)}></input><br />
                            <input type="text" value={item.components} onChange={(e) => setUpComponents(e.target.value)} /><br />
                            <input type="text" value={item.text} ></input><br />
                            <input type="text" value={item.author} onChange={(e) => setUpAuthor(e.target.value) }></input><br />
                            <button>Save</button>
                        </form>
                    </article>
                )
            })}
            
        </ main>
    );
};

export default EditRecipe;
