import { useState, useEffect } from 'react';
import './RecipesDetail.css';
import { useParams, redirect } from 'react-router-dom';

function RecipesDetail() {

    const {id} = useParams();
    const [oneRecipe, setOneRecipe] = useState(null);

    useEffect(() => {
       fetch(`http://localhost:8000/recipes/${id}`)
       .then((res) => {
            return res.json();
        })
        .then((data) => {
            setOneRecipe(data.recipe);
        });
      }, []);

      function deleteRecipe() {
        fetch(`http://localhost:8000/recipe/${id}`, {
          method: 'DELETE'
        })
         .then((res) => res.json())
         .then((data) => console.log(data));

         return redirect("/");
      }

    return(
      <main className="mainSection">
         { oneRecipe && oneRecipe.map((item) => {
           return(
              <article className="mainArticle" key={ item.id }>
                  <h1>{ item.title }</h1>
                  <div className="mainWallpaper"><img src={ item.wallpaper} /></div>
                  <h3 className="mainDescription">Sastojci</h3>
                  <div className="mainList">
                      <ul>
                          <li>20 jaja</li>
                          <li>bele kore</li>
                          <li>banane</li>
                          <li>kokos</li>
                      </ul>
                  </div>
                  <h3 className="mainDescription">Postupak</h3>
                  <p>{ item.text }</p>
                  <p>Autor: { item.author }</p>
                  <div>{ item.id }</div>
                  <button>Edit</button>
                  <button onClick={ deleteRecipe }>Delete</button>
              </article>
           )
         })}
      </main>
    );
};

export default RecipesDetail;
